import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import Test from "./Components/Test/Test";
import TestCreate from "./Components/TestCreate/TestCreate";
import Layout from "./Components/Layout/Layout";
import LogIn from "./Components/LogIn/LogIn";
import LogOff from "./Components/LogOff/LogOff";
import { BrowserRouter, Route, Routes } from "react-router-dom";
//import './index.css';
//import App from './App';
//import reportWebVitals from './reportWebVitals';

const App = () => {
  const [tests, setTests] = useState([]);
  const addTest = (test) => setTests([...tests, test]);
  const removeTest = (removeId) =>
    setTests(tests.filter(({ testId }) => testId !== removeId));
  const [user, setUser] = useState({ isAuthenticated: false, userName: "" });

  useEffect(() => {
    const getUser = async () => {
      return await fetch("https://localhost:7036/api/account/isauthenticated")
        .then((response) => {
          response.status === 401 &&
            setUser({ isAuthenticated: false, userName: "" });
          return response.json();
        })
        .then(
          (data) => {
            if (
              typeof data !== "undefined" &&
              typeof data.userName !== "undefined"
            ) {
              setUser({ isAuthenticated: true, userName: data.userName });
            }
          },
          (error) => {
            console.log(error);
          }
        );
    };
    getUser();
  }, [setUser]);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Layout user={user} />}>
          <Route index element={<h3>Главная страница</h3>} />
          <Route
            path="/tests"
            element={
              <>
                <TestCreate user={user} addTest={addTest} />
                <Test
                  user={user}
                  tests={tests}
                  setTests={setTests}
                  removeTest={removeTest}
                />
              </>
            }
          />
          <Route
            path="/login"
            element={<LogIn user={user} setUser={setUser} />}
          />
          <Route path="/logoff" element={<LogOff setUser={setUser} />} />
          <Route path="*" element={<h3>404</h3>} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  //<React.StrictMode>
  <App />
  //</React.StrictMode>
);

